// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ResolverBase} from "../../resolvers/ResolverBase.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

/**
 * @title IPaymentResolver
 * @notice Interface for the PaymentResolver profile
 */
interface IPaymentResolver {
    event X402EndpointChanged(bytes32 indexed node, string endpoint);
    event PaymentAddressChanged(
        bytes32 indexed node,
        uint256 chainId,
        address addr
    );
    event AgentMetadataChanged(bytes32 indexed node, string uri);
    event PaymentEnabledChanged(bytes32 indexed node, bool enabled);

    function x402Endpoint(bytes32 node) external view returns (string memory);
    function setX402Endpoint(bytes32 node, string calldata endpoint) external;
    function paymentAddress(
        bytes32 node,
        uint256 chainId
    ) external view returns (address);
    function setPaymentAddress(
        bytes32 node,
        uint256 chainId,
        address addr
    ) external;
    function agentMetadata(bytes32 node) external view returns (string memory);
    function setAgentMetadata(bytes32 node, string calldata uri) external;
    function paymentEnabled(bytes32 node) external view returns (bool);
    function setPaymentEnabled(bytes32 node, bool enabled) external;
    function supportedChains(
        bytes32 node
    ) external view returns (uint256[] memory);
}

/**
 * @title PaymentResolver
 * @notice Resolver profile for x402 protocol and ERC-8004 agent payments
 * @dev Implements payment endpoints, chain-specific payment addresses, and agent metadata
 */
abstract contract PaymentResolver is IPaymentResolver, ResolverBase {
    // ============ Storage ============

    /// @notice x402 payment endpoint per node
    mapping(bytes32 => string) private _x402Endpoints;

    /// @notice Payment address per node per chain
    mapping(bytes32 => mapping(uint256 => address)) private _paymentAddresses;

    /// @notice Agent metadata URI per node
    mapping(bytes32 => string) private _agentMetadata;

    /// @notice Whether payments are enabled per node
    mapping(bytes32 => bool) private _paymentEnabled;

    /// @notice Supported chain IDs per node
    mapping(bytes32 => uint256[]) private _supportedChains;

    /// @notice Track if a chain is already in the supportedChains array
    mapping(bytes32 => mapping(uint256 => bool)) private _chainExists;

    // ============ External Functions ============

    /**
     * @notice Get the x402 payment endpoint for a node
     * @param node The node to query
     * @return The x402 endpoint URL
     */
    function x402Endpoint(
        bytes32 node
    ) external view override returns (string memory) {
        return _x402Endpoints[node];
    }

    /**
     * @notice Set the x402 payment endpoint for a node
     * @param node The node to update
     * @param endpoint The x402 endpoint URL
     */
    function setX402Endpoint(
        bytes32 node,
        string calldata endpoint
    ) external override authorised(node) {
        _x402Endpoints[node] = endpoint;
        emit X402EndpointChanged(node, endpoint);
    }

    /**
     * @notice Get the payment address for a node on a specific chain
     * @param node The node to query
     * @param chainId The chain ID
     * @return The payment address
     */
    function paymentAddress(
        bytes32 node,
        uint256 chainId
    ) external view override returns (address) {
        return _paymentAddresses[node][chainId];
    }

    /**
     * @notice Set the payment address for a node on a specific chain
     * @param node The node to update
     * @param chainId The chain ID
     * @param addr The payment address
     */
    function setPaymentAddress(
        bytes32 node,
        uint256 chainId,
        address addr
    ) external override authorised(node) {
        _paymentAddresses[node][chainId] = addr;

        // Track supported chain if not already tracked
        if (!_chainExists[node][chainId]) {
            _supportedChains[node].push(chainId);
            _chainExists[node][chainId] = true;
        }

        emit PaymentAddressChanged(node, chainId, addr);
    }

    /**
     * @notice Get the agent metadata URI for a node
     * @param node The node to query
     * @return The metadata URI
     */
    function agentMetadata(
        bytes32 node
    ) external view override returns (string memory) {
        return _agentMetadata[node];
    }

    /**
     * @notice Set the agent metadata URI for a node
     * @param node The node to update
     * @param uri The metadata URI (IPFS, HTTP, etc.)
     */
    function setAgentMetadata(
        bytes32 node,
        string calldata uri
    ) external override authorised(node) {
        _agentMetadata[node] = uri;
        emit AgentMetadataChanged(node, uri);
    }

    /**
     * @notice Check if payments are enabled for a node
     * @param node The node to query
     * @return True if payments are enabled
     */
    function paymentEnabled(
        bytes32 node
    ) external view override returns (bool) {
        return _paymentEnabled[node];
    }

    /**
     * @notice Enable or disable payments for a node
     * @param node The node to update
     * @param enabled Whether to enable payments
     */
    function setPaymentEnabled(
        bytes32 node,
        bool enabled
    ) external override authorised(node) {
        _paymentEnabled[node] = enabled;
        emit PaymentEnabledChanged(node, enabled);
    }

    /**
     * @notice Get all supported chain IDs for a node
     * @param node The node to query
     * @return Array of supported chain IDs
     */
    function supportedChains(
        bytes32 node
    ) external view override returns (uint256[] memory) {
        return _supportedChains[node];
    }

    // ============ IERC165 ============

    function supportsInterface(
        bytes4 interfaceID
    ) public view virtual override returns (bool) {
        return
            interfaceID == type(IPaymentResolver).interfaceId ||
            super.supportsInterface(interfaceID);
    }
}

contracts/auction/IDDomainAuction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ~0.8.17;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {
    ReentrancyGuard
} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface INameWrapper {
    function registerAndWrapETH2LD(
        string calldata label,
        address wrappedOwner,
        uint256 duration,
        address resolver,
        uint16 ownerControlledFuses
    ) external returns (uint256 expires);
}

/**
 * @title SafuDomainAuction
 * @notice English auction system for premium .id domain names
 * @dev Supports ETH and USDC bidding with anti-sniping protection
 */
contract SafuDomainAuction is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ============ Structs ============

    struct Auction {
        string name; // Domain name (without .id)
        address seller; // Who listed (usually protocol)
        uint256 reservePrice; // Minimum price in wei/USDC
        uint256 startTime; // When auction starts
        uint256 endTime; // When auction ends
        uint256 highestBid; // Current highest bid
        address highestBidder; // Current highest bidder
        bool settled; // Whether auction has been settled
        bool isUSDC; // Whether auction is in USDC
    }

    // ============ Constants ============

    uint256 public constant MIN_AUCTION_DURATION = 1 days;
    uint256 public constant MAX_AUCTION_DURATION = 30 days;
    uint256 public constant TIME_EXTENSION = 10 minutes; // Anti-sniping
    uint256 public constant MIN_BID_INCREMENT_BPS = 500; // 5% minimum increment
    uint256 private constant LIFETIME_DURATION = 31536000000; // ~1000 years
    uint256 private constant BPS_DENOMINATOR = 10000;

    // ============ State ============

    INameWrapper public immutable nameWrapper;
    address public immutable resolver;
    IERC20 public immutable usdc;
    address public treasury;

    /// @notice auctionId => Auction
    mapping(uint256 => Auction) public auctions;
    uint256 public nextAuctionId;

    /// @notice nameHash => auctionId (for active auctions)
    mapping(bytes32 => uint256) public activeAuctions;

    /// @notice bidder => auctionId => amount (for refunds)
    mapping(address => mapping(uint256 => uint256)) public pendingReturns;

    // ============ Events ============

    event AuctionCreated(
        uint256 indexed auctionId,
        string name,
        uint256 reservePrice,
        uint256 startTime,
        uint256 endTime,
        bool isUSDC
    );

    event BidPlaced(
        uint256 indexed auctionId,
        address indexed bidder,
        uint256 amount
    );

    event AuctionExtended(uint256 indexed auctionId, uint256 newEndTime);

    event AuctionSettled(
        uint256 indexed auctionId,
        address indexed winner,
        uint256 amount
    );

    event AuctionCancelled(uint256 indexed auctionId);

    // ============ Errors ============

    error DurationTooShort();
    error DurationTooLong();
    error ReserveMustBePositive();
    error AuctionAlreadyExists();
    error AuctionNotStarted();
    error AuctionEnded();
    error AuctionNotEnded();
    error AuctionAlreadySettled();
    error BidTooLow(uint256 minBid);
    error NoBids();
    error HasBids();
    error NothingToWithdraw();
    error TransferFailed();
    error WrongPaymentType();

    // ============ Constructor ============

    constructor(
        address _nameWrapper,
        address _resolver,
        address _usdc,
        address _treasury
    ) {
        nameWrapper = INameWrapper(_nameWrapper);
        resolver = _resolver;
        usdc = IERC20(_usdc);
        treasury = _treasury;
    }

    // ============ Admin: Create Auction ============

    /**
     * @notice Create a new auction for a premium name
     * @param name Domain name (without .id)
     * @param reservePrice Minimum bid
     * @param duration Auction duration in seconds
     * @param isUSDC Whether to accept USDC instead of ETH
     */
    function createAuction(
        string calldata name,
        uint256 reservePrice,
        uint256 duration,
        bool isUSDC
    ) external onlyOwner returns (uint256 auctionId) {
        if (duration < MIN_AUCTION_DURATION) revert DurationTooShort();
        if (duration > MAX_AUCTION_DURATION) revert DurationTooLong();
        if (reservePrice == 0) revert ReserveMustBePositive();

        bytes32 nameHash = keccak256(bytes(name));
        if (activeAuctions[nameHash] != 0) revert AuctionAlreadyExists();

        auctionId = ++nextAuctionId;

        auctions[auctionId] = Auction({
            name: name,
            seller: msg.sender,
            reservePrice: reservePrice,
            startTime: block.timestamp,
            endTime: block.timestamp + duration,
            highestBid: 0,
            highestBidder: address(0),
            settled: false,
            isUSDC: isUSDC
        });

        activeAuctions[nameHash] = auctionId;

        emit AuctionCreated(
            auctionId,
            name,
            reservePrice,
            block.timestamp,
            block.timestamp + duration,
            isUSDC
        );
    }

    /**
     * @notice Batch create auctions for multiple names
     */
    function createAuctionBatch(
        string[] calldata names,
        uint256[] calldata reservePrices,
        uint256 duration,
        bool isUSDC
    ) external onlyOwner {
        require(names.length == reservePrices.length, "Length mismatch");

        for (uint256 i = 0; i < names.length; i++) {
            this.createAuction(names[i], reservePrices[i], duration, isUSDC);
        }
    }

    // ============ Bidding ============

    /**
     * @notice Place a bid on an ETH auction
     * @param auctionId The auction to bid on
     */
    function bid(uint256 auctionId) external payable nonReentrant {
        Auction storage auction = auctions[auctionId];

        if (auction.isUSDC) revert WrongPaymentType();
        if (block.timestamp < auction.startTime) revert AuctionNotStarted();
        if (block.timestamp >= auction.endTime) revert AuctionEnded();
        if (auction.settled) revert AuctionAlreadySettled();

        uint256 minBid = _getMinBid(auction);
        if (msg.value < minBid) revert BidTooLow(minBid);

        // Refund previous bidder
        if (auction.highestBidder != address(0)) {
            pendingReturns[auction.highestBidder][auctionId] += auction
                .highestBid;
        }

        auction.highestBid = msg.value;
        auction.highestBidder = msg.sender;

        // Anti-sniping: extend auction if bid in last 10 minutes
        _maybeExtendAuction(auctionId, auction);

        emit BidPlaced(auctionId, msg.sender, msg.value);
    }

    /**
     * @notice Place a bid with USDC
     * @param auctionId The auction to bid on
     * @param amount USDC amount (6 decimals)
     */
    function bidUSDC(uint256 auctionId, uint256 amount) external nonReentrant {
        Auction storage auction = auctions[auctionId];

        if (!auction.isUSDC) revert WrongPaymentType();
        if (block.timestamp < auction.startTime) revert AuctionNotStarted();
        if (block.timestamp >= auction.endTime) revert AuctionEnded();
        if (auction.settled) revert AuctionAlreadySettled();

        uint256 minBid = _getMinBid(auction);
        if (amount < minBid) revert BidTooLow(minBid);

        // Transfer USDC from bidder
        usdc.safeTransferFrom(msg.sender, address(this), amount);

        // Refund previous bidder immediately for USDC
        if (auction.highestBidder != address(0)) {
            usdc.safeTransfer(auction.highestBidder, auction.highestBid);
        }

        auction.highestBid = amount;
        auction.highestBidder = msg.sender;

        _maybeExtendAuction(auctionId, auction);

        emit BidPlaced(auctionId, msg.sender, amount);
    }

    // ============ Settlement ============

    /**
     * @notice Settle an ended auction and register the name to winner
     * @param auctionId The auction to settle
     */
    function settle(uint256 auctionId) external nonReentrant {
        Auction storage auction = auctions[auctionId];

        if (block.timestamp < auction.endTime) revert AuctionNotEnded();
        if (auction.settled) revert AuctionAlreadySettled();
        if (auction.highestBidder == address(0)) revert NoBids();

        auction.settled = true;

        // Clear active auction mapping
        bytes32 nameHash = keccak256(bytes(auction.name));
        delete activeAuctions[nameHash];

        // Transfer funds to treasury
        if (auction.isUSDC) {
            usdc.safeTransfer(treasury, auction.highestBid);
        } else {
            (bool success, ) = treasury.call{value: auction.highestBid}("");
            if (!success) revert TransferFailed();
        }

        // Register the name to winner
        nameWrapper.registerAndWrapETH2LD(
            auction.name,
            auction.highestBidder,
            LIFETIME_DURATION,
            resolver,
            0 // fuses
        );

        emit AuctionSettled(
            auctionId,
            auction.highestBidder,
            auction.highestBid
        );
    }

    /**
     * @notice Cancel an auction with no bids
     * @param auctionId The auction to cancel
     */
    function cancelAuction(uint256 auctionId) external onlyOwner {
        Auction storage auction = auctions[auctionId];

        if (auction.settled) revert AuctionAlreadySettled();
        if (auction.highestBidder != address(0)) revert HasBids();

        auction.settled = true;

        bytes32 nameHash = keccak256(bytes(auction.name));
        delete activeAuctions[nameHash];

        emit AuctionCancelled(auctionId);
    }

    // ============ Withdrawals ============

    /**
     * @notice Withdraw pending returns from being outbid
     * @param auctionId The auction to withdraw from
     */
    function withdraw(uint256 auctionId) external nonReentrant {
        uint256 amount = pendingReturns[msg.sender][auctionId];
        if (amount == 0) revert NothingToWithdraw();

        pendingReturns[msg.sender][auctionId] = 0;

        // ETH auctions use pending returns, USDC refunds immediately
        (bool success, ) = msg.sender.call{value: amount}("");
        if (!success) revert TransferFailed();
    }

    // ============ View Functions ============

    /**
     * @notice Get auction details
     */
    function getAuction(
        uint256 auctionId
    )
        external
        view
        returns (
            string memory name,
            uint256 reservePrice,
            uint256 startTime,
            uint256 endTime,
            uint256 highestBid,
            address highestBidder,
            bool settled,
            bool isUSDC
        )
    {
        Auction storage auction = auctions[auctionId];
        return (
            auction.name,
            auction.reservePrice,
            auction.startTime,
            auction.endTime,
            auction.highestBid,
            auction.highestBidder,
            auction.settled,
            auction.isUSDC
        );
    }

    /**
     * @notice Check if name has active auction
     */
    function hasActiveAuction(
        string calldata name
    ) external view returns (bool) {
        bytes32 nameHash = keccak256(bytes(name));
        uint256 auctionId = activeAuctions[nameHash];
        if (auctionId == 0) return false;

        Auction storage auction = auctions[auctionId];
        return !auction.settled && block.timestamp < auction.endTime;
    }

    /**
     * @notice Get minimum next bid for an auction
     */
    function getMinBid(uint256 auctionId) external view returns (uint256) {
        return _getMinBid(auctions[auctionId]);
    }

    // ============ Internal ============

    function _getMinBid(
        Auction storage auction
    ) internal view returns (uint256) {
        if (auction.highestBid == 0) {
            return auction.reservePrice;
        }
        return
            auction.highestBid +
            ((auction.highestBid * MIN_BID_INCREMENT_BPS) / BPS_DENOMINATOR);
    }

    function _maybeExtendAuction(
        uint256 auctionId,
        Auction storage auction
    ) internal {
        if (auction.endTime - block.timestamp < TIME_EXTENSION) {
            auction.endTime = block.timestamp + TIME_EXTENSION;
            emit AuctionExtended(auctionId, auction.endTime);
        }
    }

    // ============ Admin ============

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
    }

    receive() external payable {}
}

contracts/dnsregistrar/DNSClaimChecker.s.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../dnssec-oracle/DNSSEC.sol";
import "../dnssec-oracle/RRUtils.sol";
import "../utils/BytesUtils.sol";
import "../utils/HexUtils.sol";
import "@ensdomains/buffer/contracts/Buffer.sol";

library DNSClaimChecker {
    using BytesUtils for bytes;
    using HexUtils for bytes;
    using RRUtils for *;
    using Buffer for Buffer.buffer;

    uint16 constant CLASS_INET = 1;
    uint16 constant TYPE_TXT = 16;

    function getOwnerAddress(
        bytes memory name,
        bytes memory data
    ) internal pure returns (address, bool) {
        // Add "_ens." to the front of the name.
        Buffer.buffer memory buf;
        buf.init(name.length + 5);
        buf.append("\x04_ens");
        buf.append(name);

        for (
            RRUtils.RRIterator memory iter = data.iterateRRs(0);
            !iter.done();
            iter.next()
        ) {
            if (iter.name().compareNames(buf.buf) != 0) continue;
            bool found;
            address addr;
            (addr, found) = parseRR(data, iter.rdataOffset, iter.nextOffset);
            if (found) {
                return (addr, true);
            }
        }

        return (address(0x0), false);
    }

    function parseRR(
        bytes memory rdata,
        uint256 idx,
        uint256 endIdx
    ) internal pure returns (address, bool) {
        while (idx < endIdx) {
            uint256 len = rdata.readUint8(idx);
            idx += 1;

            bool found;
            address addr;
            (addr, found) = parseString(rdata, idx, len);

            if (found) return (addr, true);
            idx += len;
        }

        return (address(0x0), false);
    }

    function parseString(
        bytes memory str,
        uint256 idx,
        uint256 len
    ) internal pure returns (address, bool) {
        // TODO: More robust parsing that handles whitespace and multiple key/value pairs
        if (str.readUint32(idx) != 0x613d3078) return (address(0x0), false); // 0x613d3078 == 'a=0x'
        return str.hexToAddress(idx + 4, idx + len);
    }
}

contracts/dnsregistrar/DNSRegistrar.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@ensdomains/buffer/contracts/Buffer.sol";
import "../dnssec-oracle/DNSSEC.sol";
import "../dnssec-oracle/RRUtils.sol";
import "../registry/ENSRegistry.sol";
import "../root/Root.sol";
import "../resolvers/profiles/AddrResolver.sol";
import "../utils/BytesUtils.sol";
import "./DNSClaimChecker.sol";
import "./PublicSuffixList.sol";
import "./IDNSRegistrar.sol";

/**
 * @dev An ENS registrar that allows the owner of a DNS name to claim the
 *      corresponding name in ENS.
 */
contract DNSRegistrar is IDNSRegistrar, IERC165 {
    using BytesUtils for bytes;
    using Buffer for Buffer.buffer;
    using RRUtils for *;

    ENS public immutable ens;
    DNSSEC public immutable oracle;
    PublicSuffixList public suffixes;
    address public immutable previousRegistrar;
    address public immutable resolver;
    // A mapping of the most recent signatures seen for each claimed domain.
    mapping(bytes32 => uint32) public inceptions;

    error NoOwnerRecordFound();
    error PermissionDenied(address caller, address owner);
    error PreconditionNotMet();
    error StaleProof();
    error InvalidPublicSuffix(bytes name);

    struct OwnerRecord {
        bytes name;
        address owner;
        address resolver;
        uint64 ttl;
    }

    event Claim(
        bytes32 indexed node,
        address indexed owner,
        bytes dnsname,
        uint32 inception
    );
    event NewPublicSuffixList(address suffixes);

    constructor(
        address _previousRegistrar,
        address _resolver,
        DNSSEC _dnssec,
        PublicSuffixList _suffixes,
        ENS _ens
    ) {
        previousRegistrar = _previousRegistrar;
        resolver = _resolver;
        oracle = _dnssec;
        suffixes = _suffixes;
        emit NewPublicSuffixList(address(suffixes));
        ens = _ens;
    }

    /**
     * @dev This contract's owner-only functions can be invoked by the owner of the ENS root.
     */
    modifier onlyOwner() {
        Root root = Root(ens.owner(bytes32(0)));
        address owner = root.owner();
        require(msg.sender == owner);
        _;
    }

    function setPublicSuffixList(PublicSuffixList _suffixes) public onlyOwner {
        suffixes = _suffixes;
        emit NewPublicSuffixList(address(suffixes));
    }

    /**
     * @dev Submits proofs to the DNSSEC oracle, then claims a name using those proofs.
     * @param name The name to claim, in DNS wire format.
     * @param input A chain of signed DNS RRSETs ending with a text record.
     */
    function proveAndClaim(
        bytes memory name,
        DNSSEC.RRSetWithSignature[] memory input
    ) public override {
        (bytes32 rootNode, bytes32 labelHash, address addr) = _claim(
            name,
            input
        );
        ens.setSubnodeOwner(rootNode, labelHash, addr);
    }

    function proveAndClaimWithResolver(
        bytes memory name,
        DNSSEC.RRSetWithSignature[] memory input,
        address resolver,
        address addr
    ) public override {
        (bytes32 rootNode, bytes32 labelHash, address owner) = _claim(
            name,
            input
        );
        if (msg.sender != owner) {
            revert PermissionDenied(msg.sender, owner);
        }
        ens.setSubnodeRecord(rootNode, labelHash, owner, resolver, 0);
        if (addr != address(0)) {
            if (resolver == address(0)) {
                revert PreconditionNotMet();
            }
            bytes32 node = keccak256(abi.encodePacked(rootNode, labelHash));
            // Set the resolver record
            AddrResolver(resolver).setAddr(node, addr);
        }
    }

    function supportsInterface(
        bytes4 interfaceID
    ) external pure override returns (bool) {
        return
            interfaceID == type(IERC165).interfaceId ||
            interfaceID == type(IDNSRegistrar).interfaceId;
    }

    function _claim(
        bytes memory name,
        DNSSEC.RRSetWithSignature[] memory input
    ) internal returns (bytes32 parentNode, bytes32 labelHash, address addr) {
        (bytes memory data, uint32 inception) = oracle.verifyRRSet(input);

        // Get the first label
        uint256 labelLen = name.readUint8(0);
        labelHash = name.keccak(1, labelLen);

        bytes memory parentName = name.substring(
            labelLen + 1,
            name.length - labelLen - 1
        );

        // Make sure the parent name is enabled
        parentNode = enableNode(parentName);

        bytes32 node = keccak256(abi.encodePacked(parentNode, labelHash));
        if (!RRUtils.serialNumberGte(inception, inceptions[node])) {
            revert StaleProof();
        }
        inceptions[node] = inception;

        bool found;
        (addr, found) = DNSClaimChecker.getOwnerAddress(name, data);
        if (!found) {
            revert NoOwnerRecordFound();
        }

        emit Claim(node, addr, name, inception);
    }

    function enableNode(bytes memory domain) public returns (bytes32 node) {
        // Name must be in the public suffix list.
        if (!suffixes.isPublicSuffix(domain)) {
            revert InvalidPublicSuffix(domain);
        }
        return _enableNode(domain, 0);
    }

    function _enableNode(
        bytes memory domain,
        uint256 offset
    ) internal returns (bytes32 node) {
        uint256 len = domain.readUint8(offset);
        if (len == 0) {
            return bytes32(0);
        }

        bytes32 parentNode = _enableNode(domain, offset + len + 1);
        bytes32 label = domain.keccak(offset + 1, len);
        node = keccak256(abi.encodePacked(parentNode, label));
        address owner = ens.owner(node);
        if (owner == address(0) || owner == previousRegistrar) {
            if (parentNode == bytes32(0)) {
                Root root = Root(ens.owner(bytes32(0)));
                root.setSubnodeOwner(label, address(this));
                ens.setResolver(node, resolver);
            } else {
                ens.setSubnodeRecord(
                    parentNode,
                    label,
                    address(this),
                    resolver,
                    0
                );
            }
        } else if (owner != address(this)) {
            revert PreconditionNotMet();
        }
        return node;
    }
}

