//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../utils/BytesUtils.sol";
import "@ensdomains/buffer/contracts/Buffer.sol";

/**
 * @dev RRUtils is a library that provides utilities for parsing DNS resource records.
 */
library RRUtils {
    using BytesUtils for *;
    using Buffer for *;

    /**
     * @dev Returns the number of bytes in the DNS name at 'offset' in 'self'.
     * @param self The byte array to read a name from.
     * @param offset The offset to start reading at.
     * @return The length of the DNS name at 'offset', in bytes.
     */
    function nameLength(
        bytes memory self,
        uint256 offset
    ) internal pure returns (uint256) {
        uint256 idx = offset;
        while (true) {
            assert(idx < self.length);
            uint256 labelLen = self.readUint8(idx);
            idx += labelLen + 1;
            if (labelLen == 0) {
                break;
            }
        }
        return idx - offset;
    }

    /**
     * @dev Returns a DNS format name at the specified offset of self.
     * @param self The byte array to read a name from.
     * @param offset The offset to start reading at.
     * @return ret The name.
     */
    function readName(
        bytes memory self,
        uint256 offset
    ) internal pure returns (bytes memory ret) {
        uint256 len = nameLength(self, offset);
        return self.substring(offset, len);
    }

    /**
     * @dev Returns the number of labels in the DNS name at 'offset' in 'self'.
     * @param self The byte array to read a name from.
     * @param offset The offset to start reading at.
     * @return The number of labels in the DNS name at 'offset', in bytes.
     */
    function labelCount(
        bytes memory self,
        uint256 offset
    ) internal pure returns (uint256) {
        uint256 count = 0;
        while (true) {
            assert(offset < self.length);
            uint256 labelLen = self.readUint8(offset);
            offset += labelLen + 1;
            if (labelLen == 0) {
                break;
            }
            count += 1;
        }
        return count;
    }

    uint256 constant RRSIG_TYPE = 0;
    uint256 constant RRSIG_ALGORITHM = 2;
    uint256 constant RRSIG_LABELS = 3;
    uint256 constant RRSIG_TTL = 4;
    uint256 constant RRSIG_EXPIRATION = 8;
    uint256 constant RRSIG_INCEPTION = 12;
    uint256 constant RRSIG_KEY_TAG = 16;
    uint256 constant RRSIG_SIGNER_NAME = 18;

    struct SignedSet {
        uint16 typeCovered;
        uint8 algorithm;
        uint8 labels;
        uint32 ttl;
        uint32 expiration;
        uint32 inception;
        uint16 keytag;
        bytes signerName;
        bytes data;
        bytes name;
    }

    function readSignedSet(
        bytes memory data
    ) internal pure returns (SignedSet memory self) {
        self.typeCovered = data.readUint16(RRSIG_TYPE);
        self.algorithm = data.readUint8(RRSIG_ALGORITHM);
        self.labels = data.readUint8(RRSIG_LABELS);
        self.ttl = data.readUint32(RRSIG_TTL);
        self.expiration = data.readUint32(RRSIG_EXPIRATION);
        self.inception = data.readUint32(RRSIG_INCEPTION);
        self.keytag = data.readUint16(RRSIG_KEY_TAG);
        self.signerName = readName(data, RRSIG_SIGNER_NAME);
        self.data = data.substring(
            RRSIG_SIGNER_NAME + self.signerName.length,
            data.length - RRSIG_SIGNER_NAME - self.signerName.length
        );
    }

    function rrs(
        SignedSet memory rrset
    ) internal pure returns (RRIterator memory) {
        return iterateRRs(rrset.data, 0);
    }

    /**
     * @dev An iterator over resource records.
     */
    struct RRIterator {
        bytes data;
        uint256 offset;
        uint16 dnstype;
        uint16 class;
        uint32 ttl;
        uint256 rdataOffset;
        uint256 nextOffset;
    }

    /**
     * @dev Begins iterating over resource records.
     * @param self The byte string to read from.
     * @param offset The offset to start reading at.
     * @return ret An iterator object.
     */
    function iterateRRs(
        bytes memory self,
        uint256 offset
    ) internal pure returns (RRIterator memory ret) {
        ret.data = self;
        ret.nextOffset = offset;
        next(ret);
    }

    /**
     * @dev Returns true iff there are more RRs to iterate.
     * @param iter The iterator to check.
     * @return True iff the iterator has finished.
     */
    function done(RRIterator memory iter) internal pure returns (bool) {
        return iter.offset >= iter.data.length;
    }

    /**
     * @dev Moves the iterator to the next resource record.
     * @param iter The iterator to advance.
     */
    function next(RRIterator memory iter) internal pure {
        iter.offset = iter.nextOffset;
        if (iter.offset >= iter.data.length) {
            return;
        }

        // Skip the name
        uint256 off = iter.offset + nameLength(iter.data, iter.offset);

        // Read type, class, and ttl
        iter.dnstype = iter.data.readUint16(off);
        off += 2;
        iter.class = iter.data.readUint16(off);
        off += 2;
        iter.ttl = iter.data.readUint32(off);
        off += 4;

        // Read the rdata
        uint256 rdataLength = iter.data.readUint16(off);
        off += 2;
        iter.rdataOffset = off;
        iter.nextOffset = off + rdataLength;
    }

    /**
     * @dev Returns the name of the current record.
     * @param iter The iterator.
     * @return A new bytes object containing the owner name from the RR.
     */
    function name(RRIterator memory iter) internal pure returns (bytes memory) {
        return
            iter.data.substring(
                iter.offset,
                nameLength(iter.data, iter.offset)
            );
    }

    /**
     * @dev Returns the rdata portion of the current record.
     * @param iter The iterator.
     * @return A new bytes object containing the RR's RDATA.
     */
    function rdata(
        RRIterator memory iter
    ) internal pure returns (bytes memory) {
        return
            iter.data.substring(
                iter.rdataOffset,
                iter.nextOffset - iter.rdataOffset
            );
    }

    uint256 constant DNSKEY_FLAGS = 0;
    uint256 constant DNSKEY_PROTOCOL = 2;
    uint256 constant DNSKEY_ALGORITHM = 3;
    uint256 constant DNSKEY_PUBKEY = 4;

    struct DNSKEY {
        uint16 flags;
        uint8 protocol;
        uint8 algorithm;
        bytes publicKey;
    }

    function readDNSKEY(
        bytes memory data,
        uint256 offset,
        uint256 length
    ) internal pure returns (DNSKEY memory self) {
        self.flags = data.readUint16(offset + DNSKEY_FLAGS);
        self.protocol = data.readUint8(offset + DNSKEY_PROTOCOL);
        self.algorithm = data.readUint8(offset + DNSKEY_ALGORITHM);
        self.publicKey = data.substring(
            offset + DNSKEY_PUBKEY,
            length - DNSKEY_PUBKEY
        );
    }

    uint256 constant DS_KEY_TAG = 0;
    uint256 constant DS_ALGORITHM = 2;
    uint256 constant DS_DIGEST_TYPE = 3;
    uint256 constant DS_DIGEST = 4;

    struct DS {
        uint16 keytag;
        uint8 algorithm;
        uint8 digestType;
        bytes digest;
    }

    function readDS(
        bytes memory data,
        uint256 offset,
        uint256 length
    ) internal pure returns (DS memory self) {
        self.keytag = data.readUint16(offset + DS_KEY_TAG);
        self.algorithm = data.readUint8(offset + DS_ALGORITHM);
        self.digestType = data.readUint8(offset + DS_DIGEST_TYPE);
        self.digest = data.substring(offset + DS_DIGEST, length - DS_DIGEST);
    }

    function isSubdomainOf(
        bytes memory self,
        bytes memory other
    ) internal pure returns (bool) {
        uint256 off = 0;
        uint256 counts = labelCount(self, 0);
        uint256 othercounts = labelCount(other, 0);

        while (counts > othercounts) {
            off = progress(self, off);
            counts--;
        }

        return self.equals(off, other, 0);
    }

    function compareNames(
        bytes memory self,
        bytes memory other
    ) internal pure returns (int256) {
        if (self.equals(other)) {
            return 0;
        }

        uint256 off;
        uint256 otheroff;
        uint256 prevoff;
        uint256 otherprevoff;
        uint256 counts = labelCount(self, 0);
        uint256 othercounts = labelCount(other, 0);

        // Keep removing labels from the front of the name until both names are equal length
        while (counts > othercounts) {
            prevoff = off;
            off = progress(self, off);
            counts--;
        }

        while (othercounts > counts) {
            otherprevoff = otheroff;
            otheroff = progress(other, otheroff);
            othercounts--;
        }

        // Compare the last nonequal labels to each other
        while (counts > 0 && !self.equals(off, other, otheroff)) {
            prevoff = off;
            off = progress(self, off);
            otherprevoff = otheroff;
            otheroff = progress(other, otheroff);
            counts -= 1;
        }

        if (off == 0) {
            return -1;
        }
        if (otheroff == 0) {
            return 1;
        }

        return
            self.compare(
                prevoff + 1,
                self.readUint8(prevoff),
                other,
                otherprevoff + 1,
                other.readUint8(otherprevoff)
            );
    }

    /**
     * @dev Compares two serial numbers using RFC1982 serial number math.
     */
    function serialNumberGte(
        uint32 i1,
        uint32 i2
    ) internal pure returns (bool) {
        unchecked {
            return int32(i1) - int32(i2) >= 0;
        }
    }

    function progress(
        bytes memory body,
        uint256 off
    ) internal pure returns (uint256) {
        return off + 1 + body.readUint8(off);
    }

    /**
     * @dev Computes the keytag for a chunk of data.
     * @param data The data to compute a keytag for.
     * @return The computed key tag.
     */
    function computeKeytag(bytes memory data) internal pure returns (uint16) {
        /* This function probably deserves some explanation.
         * The DNSSEC keytag function is a checksum that relies on summing up individual bytes
         * from the input string, with some mild bitshifting. Here's a Naive solidity implementation:
         *
         *     function computeKeytag(bytes memory data) internal pure returns (uint16) {
         *         uint ac;
         *         for (uint i = 0; i < data.length; i++) {
         *             ac += i & 1 == 0 ? uint16(data.readUint8(i)) << 8 : data.readUint8(i);
         *         }
         *         return uint16(ac + (ac >> 16));
         *     }
         *
         * The EVM, with its 256 bit words, is exceedingly inefficient at doing byte-by-byte operations;
         * the code above, on reasonable length inputs, consumes over 100k gas. But we can make the EVM's
         * large words work in our favour.
         *
         * The code below works by treating the input as a series of 256 bit words. It first masks out
         * even and odd bytes from each input word, adding them to two separate accumulators `ac1` and `ac2`.
         * The bytes are separated by empty bytes, so as long as no individual sum exceeds 2^16-1, we're
         * effectively summing 16 different numbers with each EVM ADD opcode.
         *
         * Once it's added up all the inputs, it has to add all the 16 bit values in `ac1` and `ac2` together.
         * It does this using the same trick - mask out every other value, shift to align them, add them together.
         * After the first addition on both accumulators, there's enough room to add the two accumulators together,
         * and the remaining sums can be done just on ac1.
         */
        unchecked {
            require(data.length <= 8192, "Long keys not permitted");
            uint256 ac1;
            uint256 ac2;
            for (uint256 i = 0; i < data.length + 31; i += 32) {
                uint256 word;
                assembly {
                    word := mload(add(add(data, 32), i))
                }
                if (i + 32 > data.length) {
                    uint256 unused = 256 - (data.length - i) * 8;
                    word = (word >> unused) << unused;
                }
                ac1 +=
                    (word &
                        0xFF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00) >>
                    8;
                ac2 += (word &
                    0x00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF00FF);
            }
            ac1 =
                (ac1 &
                    0x0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF) +
                ((ac1 &
                    0xFFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000) >>
                    16);
            ac2 =
                (ac2 &
                    0x0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF) +
                ((ac2 &
                    0xFFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000FFFF0000) >>
                    16);
            ac1 = (ac1 << 8) + ac2;
            ac1 =
                (ac1 &
                    0x00000000FFFFFFFF00000000FFFFFFFF00000000FFFFFFFF00000000FFFFFFFF) +
                ((ac1 &
                    0xFFFFFFFF00000000FFFFFFFF00000000FFFFFFFF00000000FFFFFFFF00000000) >>
                    32);
            ac1 =
                (ac1 &
                    0x0000000000000000FFFFFFFFFFFFFFFF0000000000000000FFFFFFFFFFFFFFFF) +
                ((ac1 &
                    0xFFFFFFFFFFFFFFFF0000000000000000FFFFFFFFFFFFFFFF0000000000000000) >>
                    64);
            ac1 =
                (ac1 &
                    0x00000000000000000000000000000000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF) +
                (ac1 >> 128);
            ac1 += (ac1 >> 16) & 0xFFFF;
            return uint16(ac1);
        }
    }
}

contracts/ethregistrar/Airdrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Airdrop is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // point accounting
    mapping(address => uint256) private points;
    mapping(address => bool) private isMinter; // prevents duplicates in minters array
    address[] private minters; // list of users with >0 points
    uint256 public totalPoints;
    mapping(address => bool) public controllers;

    // configurable cap: optionally limit total points
    uint256 public pointsCap = 1_000_000;

    // events
    event PointsUpdated(
        address indexed user,
        uint256 added,
        uint256 newBalance
    );
    event PointsBatchUpdated(uint256 count);
    event PointsCapUpdated(uint256 newCap);
    event TokensDistributed(address indexed token, uint256 totalAmount);
    event EmergencyWithdraw(address token, address to, uint256 amount);

    constructor() {
        // Ownable sets owner = msg.sender automatically
    }

    // ------------------------
    // Owner functions
    // ------------------------

    modifier onlyController {
        require(controllers[msg.sender] == true, "Not a Controller");
        _;
    }

    function setController(address controller) external {
        controllers[controller] = true;
    }

    /// @notice update points for a single user
    function updatePoints(address user, uint256 addPoints) external onlyController {
        require(user != address(0), "bad addr");
        require(addPoints > 0, "zero points");
        if (totalPoints + addPoints <= pointsCap) {
            uint256 prev = points[user];
            points[user] = prev + addPoints;
            totalPoints += addPoints;

            // if this is the user's first time, add to minters list
            if (!isMinter[user]) {
                isMinter[user] = true;
                minters.push(user);
            }
            emit PointsUpdated(user, addPoints, points[user]);
        }
    }

    /// @notice batch update (addresses[] and points[] must match length)
    function batchUpdatePoints(
        address[] calldata users,
        uint256[] calldata addPoints
    ) external onlyController {
        require(users.length == addPoints.length, "len mismatch");
        uint256 len = users.length;
        uint256 sum = 0;

        for (uint256 i = 0; i < len; i++) {
            address u = users[i];
            uint256 v = addPoints[i];
            require(u != address(0), "bad addr");
            require(v > 0, "zero points");
            // defensive check to avoid overflow on cap
            require(totalPoints + sum + v <= pointsCap, "cap exceeded");

            uint256 prev = points[u];
            points[u] = prev + v;
            if (!isMinter[u]) {
                isMinter[u] = true;
                minters.push(u);
            }
            sum += v;
            emit PointsUpdated(u, v, points[u]);
        }

        totalPoints += sum;
        emit PointsBatchUpdated(len);
    }

    function setPointsCap(uint256 newCap) external onlyOwner {
        pointsCap = newCap;
        emit PointsCapUpdated(newCap);
    }

    // ------------------------
    // Distribution (caution: gas heavy)
    // ------------------------

    /**
     * @notice distribute `amount` of `token` to all recorded minters proportionally to their points.
     * @dev This loops over minters[] and will fail/gas out if minters array is huge.
     * Use Merkle airdrop for large lists (recommended).
     */
    function distributeERC20(
        address token,
        uint256 amount
    ) external onlyOwner nonReentrant {
        require(amount > 0, "zero amount");
        require(totalPoints > 0, "no points");
        IERC20 erc = IERC20(token);

        // pull tokens from owner/treasury into this contract first (or owner must have approved this contract)
        // safer pattern: require that contract already has `amount` balance
        uint256 balance = erc.balanceOf(address(this));
        require(balance >= amount, "insufficient contract balance");

        uint256 len = minters.length;
        for (uint256 i = 0; i < len; i++) {
            address user = minters[i];
            uint256 userPoints = points[user];
            if (userPoints == 0) continue; // should not happen, but defensive
            uint256 share = (amount * userPoints) / totalPoints;
            if (share > 0) {
                erc.safeTransfer(user, share);
            }
        }

        emit TokensDistributed(token, amount);
    }

    // ------------------------
    // Views
    // ------------------------

    function getTotalPoints() external view returns (uint256) {
        return totalPoints;
    }

    function getUserPoints(address user) external view returns (uint256) {
        return points[user];
    }

    function getAllUsers() external view returns (address[] memory) {
        return minters;
    }

    function getAllUserPoints(
        address[] calldata users
    ) external view returns (uint256[] memory) {
        uint256[] memory _points = new uint256[](users.length);
        for (uint256 i = 0; i < users.length; i++) {
            _points[i] = points[users[i]];
        }
        return _points;
    }

    // ------------------------
    // Emergency / admin
    // ------------------------

    /// @notice withdraw any ERC20 accidentally sent to contract (owner only)
    function emergencyWithdrawERC20(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner {
        require(to != address(0), "bad to");
        IERC20(token).safeTransfer(to, amount);
        emit EmergencyWithdraw(token, to, amount);
    }

    /// @notice withdraw native balance
    function emergencyWithdrawBNB(
        address payable to,
        uint256 amount
    ) external onlyOwner {
        require(to != payable(address(0)), "bad to");
        (bool ok, ) = to.call{value: amount}("");
        require(ok, "transfer failed");
        emit EmergencyWithdraw(address(0), to, amount);
    }

    // fallback receive
    receive() external payable {}
}

contracts/ethregistrar/BaseRegistrarImpl.content:
pragma solidity >=0.8.4;

import "../registry/ENS.sol";
import "./IBaseRegistrar.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import {IERC2981} from "../utils/IERC2981.sol";

contract BaseRegistrarImplementation is
    ERC721,
    IBaseRegistrar,
    Ownable,
    IERC2981
{
    // ============ Royalty Constants ============
    uint96 public constant ROYALTY_BPS = 500; // 5% = 500 basis points
    uint96 private constant BPS_DENOMINATOR = 10000;

    // ============ Royalty State ============
    address public royaltyReceiver;

    // ============ Royalty Events ============
    event RoyaltyReceiverUpdated(
        address indexed oldReceiver,
        address indexed newReceiver
    );
    // A map of expiry times
    mapping(uint256 => uint256) expiries;
    // The ENS registry
    ENS public ens;
    // The namehash of the TLD this registrar owns (eg, .eth)
    bytes32 public baseNode;
    // A map of addresses that are authorised to register and renew names.
    mapping(address => bool) public controllers;
    uint256 public constant GRACE_PERIOD = 30 days;
    uint256 public constant LIFETIME = 31536000000;
    bytes4 private constant INTERFACE_META_ID =
        bytes4(keccak256("supportsInterface(bytes4)"));
    bytes4 private constant ERC721_ID =
        bytes4(
            keccak256("balanceOf(address)") ^
                keccak256("ownerOf(uint256)") ^
                keccak256("approve(address,uint256)") ^
                keccak256("getApproved(uint256)") ^
                keccak256("setApprovalForAll(address,bool)") ^
                keccak256("isApprovedForAll(address,address)") ^
                keccak256("transferFrom(address,address,uint256)") ^
                keccak256("safeTransferFrom(address,address,uint256)") ^
                keccak256("safeTransferFrom(address,address,uint256,bytes)")
        );
    bytes4 private constant RECLAIM_ID =
        bytes4(keccak256("reclaim(uint256,address)"));

    /// v2.1.3 version of _isApprovedOrOwner which calls ownerOf(tokenId) and takes grace period into consideration instead of ERC721.ownerOf(tokenId);
    /// https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v2.1.3/contracts/token/ERC721/ERC721.sol#L187
    /// @dev Returns whether the given spender can transfer a given token ID
    /// @param spender address of the spender to query
    /// @param tokenId uint256 ID of the token to be transferred
    /// @return bool whether the msg.sender is approved for the given token ID,
    ///              is an operator of the owner, or is the owner of the token
    function _isApprovedOrOwner(
        address spender,
        uint256 tokenId
    ) internal view override returns (bool) {
        address owner = ownerOf(tokenId);
        return (spender == owner ||
            getApproved(tokenId) == spender ||
            isApprovedForAll(owner, spender));
    }

    constructor(ENS _ens, bytes32 _baseNode) ERC721("", "") {
        ens = _ens;
        baseNode = _baseNode;
        Ownable(msg.sender);
    }

    modifier live() {
        require(ens.owner(baseNode) == address(this));
        _;
    }

    modifier onlyController() {
        require(controllers[msg.sender]);
        _;
    }

    /// @dev Gets the owner of the specified token ID. Names become unowned
    ///      when their registration expires.
    /// @param tokenId uint256 ID of the token to query the owner of
    /// @return address currently marked as the owner of the given token ID
    function ownerOf(
        uint256 tokenId
    ) public view override(IERC721, ERC721) returns (address) {
        require(expiries[tokenId] > block.timestamp);
        return super.ownerOf(tokenId);
    }

    // Authorises a controller, who can register and renew domains.
    function addController(address controller) external override onlyOwner {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    // Revoke controller permission for an address.
    function removeController(address controller) external override onlyOwner {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }

    // Set the resolver for the TLD this registrar manages.
    function setResolver(address resolver) external override onlyOwner {
        ens.setResolver(baseNode, resolver);
    }

    // Returns the expiration timestamp of the specified id.
    function nameExpires(uint256 id) external view override returns (uint256) {
        return expiries[id];
    }

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) public view override returns (bool) {
        // Not available if it's registered here or in its grace period.
        return expiries[id] + GRACE_PERIOD < block.timestamp;
    }

    /// @dev Register a name.
    /// @param id The token ID (keccak256 of the label).
    /// @param owner The address that should own the registration.
    /// @param duration Duration in seconds for the registration.
    function register(
        uint256 id,
        address owner,
        uint256 duration
    ) external virtual override returns (uint256) {
        return _register(id, owner, duration, true);
    }

    /// @dev Register a name, without modifying the registry.
    /// @param id The token ID (keccak256 of the label).
    /// @param owner The address that should own the registration.
    /// @param duration Duration in seconds for the registration.
    function registerOnly(
        uint256 id,
        address owner,
        uint256 duration
    ) external returns (uint256) {
        return _register(id, owner, duration, false);
    }

    function _register(
        uint256 id,
        address owner,
        uint256 duration,
        bool updateRegistry
    ) internal live onlyController returns (uint256) {
        require(available(id));
        require(
            block.timestamp + duration + GRACE_PERIOD >
                block.timestamp + GRACE_PERIOD
        ); // Prevent future overflow

        uint256 expiration;

        if (duration == 31536000000) {
            // Lifetime registration
            expiration = LIFETIME;
        } else {
            // Annual registration
            expiration = block.timestamp + duration;
        }

        expiries[id] = expiration;
        if (_exists(id)) {
            // Name was previously owned, and expired
            _burn(id);
        }
        _mint(owner, id);
        if (updateRegistry) {
            ens.setSubnodeOwner(baseNode, bytes32(id), owner);
        }

        emit NameRegistered(id, owner, expiration);

        return expiration;
    }

    function renew(
        uint256 id,
        uint256 duration
    ) external virtual override live onlyController returns (uint256) {
        require(expiries[id] + GRACE_PERIOD >= block.timestamp); // Name must be registered here or in grace period
        require(expiries[id] != LIFETIME, "Lifetime names cannot be renewed");
        require(
            expiries[id] + duration + GRACE_PERIOD > duration + GRACE_PERIOD
        ); // Prevent future overflow
        if (duration == 31536000000) {
            // Lifetime registration
            expiries[id] = LIFETIME;
            emit NameRenewed(id, expiries[id]);
            return expiries[id];
        } else {
            expiries[id] += duration;
        }
        emit NameRenewed(id, expiries[id]);
        return expiries[id];
    }

    /// @dev Reclaim ownership of a name in ENS, if you own it in the registrar.
    function reclaim(uint256 id, address owner) external override live {
        require(_isApprovedOrOwner(msg.sender, id));
        ens.setSubnodeOwner(baseNode, bytes32(id), owner);
    }

    function supportsInterface(
        bytes4 interfaceID
    ) public view override(ERC721, IERC165) returns (bool) {
        return
            interfaceID == INTERFACE_META_ID ||
            interfaceID == ERC721_ID ||
            interfaceID == RECLAIM_ID ||
            interfaceID == type(IERC2981).interfaceId;
    }

    // ============ ERC-2981 Royalty Implementation ============

    /**
     * @notice Returns royalty info for a given token and sale price
     * @param salePrice The sale price of the NFT
     * @return receiver Address to receive royalty
     * @return royaltyAmount The royalty amount (5% of sale price)
     */
    function royaltyInfo(
        uint256,
        uint256 salePrice
    ) external view override returns (address receiver, uint256 royaltyAmount) {
        receiver = royaltyReceiver;
        royaltyAmount = (salePrice * ROYALTY_BPS) / BPS_DENOMINATOR;
    }

    /**
     * @notice Update the royalty receiver address
     * @param _newReceiver New address to receive royalties
     */
    function setRoyaltyReceiver(address _newReceiver) external onlyOwner {
        require(_newReceiver != address(0), "Invalid receiver");
        address oldReceiver = royaltyReceiver;
        royaltyReceiver = _newReceiver;
        emit RoyaltyReceiverUpdated(oldReceiver, _newReceiver);
    }

    function _exists(uint256 tokenId) internal view override returns (bool) {
        return super._exists(tokenId);
    }
}

contracts/ethregistrar/BulkRenewal.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ~0.8.17;

import "../registry/ENS.sol";
import "./ETHRegistrarController.sol";
import "./IETHRegistrarController.sol";
import "../resolvers/Resolver.sol";
import "./IBulkRenewal.sol";
import "./IPriceOracle.sol";

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";

contract BulkRenewal is IBulkRenewal {
    bytes32 private constant ETH_NAMEHASH =
        0xe3f6fa8ec34a0592261cf8313365d7f76784e190d0c33fbd4d51f8461a9b8e54;

    ENS public immutable ens;

    constructor(ENS _ens) {
        ens = _ens;
    }

    function getController() internal view returns (ETHRegistrarController) {
        Resolver r = Resolver(ens.resolver(ETH_NAMEHASH));
        return
            ETHRegistrarController(
                r.interfaceImplementer(
                    ETH_NAMEHASH,
                    type(IETHRegistrarController).interfaceId
                )
            );
    }

    function rentPrice(
        string[] calldata names,
        uint256 duration,
        bool lifetime
    ) external view override returns (uint256 total) {
        ETHRegistrarController controller = getController();
        uint256 length = names.length;
        for (uint256 i = 0; i < length; ) {
            IPriceOracle.Price memory price = controller.rentPrice(
                names[i],
                duration,
                lifetime
            );
            unchecked {
                ++i;
                total += (price.base + price.premium);
            }
        }
    }

    function renewAll(
        string[] calldata names,
        uint256 duration,
        bool lifetime
    ) external payable override {
        ETHRegistrarController controller = getController();
        uint256 length = names.length;
        uint256 total;
        for (uint256 i = 0; i < length; ) {
            IPriceOracle.Price memory price = controller.rentPrice(
                names[i],
                duration,
                lifetime
            );
            uint256 totalPrice = price.base + price.premium;
            controller.renew{value: totalPrice}(names[i], duration, lifetime);
            unchecked {
                ++i;
                total += totalPrice;
            }
        }
        // Send any excess funds back
        payable(msg.sender).transfer(address(this).balance);
    }

    function supportsInterface(
        bytes4 interfaceID
    ) external pure returns (bool) {
        return
            interfaceID == type(IERC165).interfaceId ||
            interfaceID == type(IBulkRenewal).interfaceId;
    }
}

contracts/ethregistrar/ETHRegistrarContr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {BaseRegistrarImplementation} from "./BaseRegistrarImplementation.sol";
import {StringUtils} from "../utils/StringUtils.sol";
import {Resolver} from "../resolvers/Resolver.sol";
import {ENS} from "../registry/ENS.sol";
import {ReverseRegistrar} from "../reverseRegistrar/ReverseRegistrar.sol";
import {ReverseClaimer} from "../reverseRegistrar/ReverseClaimer.sol";
import {
    IETHRegistrarController,
    IPriceOracle
} from "./IETHRegistrarController.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {INameWrapper} from "../wrapper/INameWrapper.sol";
import {ERC20Recoverable} from "../utils/ERC20Recoverable.sol";
import {TokenPriceOracle} from "./TokenPriceOracle.sol";
import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReferralVerifier} from "./ReferralVerifier.sol";
import {Airdrop} from "./Airdrop.sol";

error CommitmentTooNew(bytes32 commitment);
error CommitmentTooOld(bytes32 commitment);
error NameNotAvailable(string name);
error DurationTooShort(uint256 duration);
error ResolverRequiredWhenDataSupplied();
error UnexpiredCommitmentExists(bytes32 commitment);
error InsufficientValue();
error MaxCommitmentAgeTooLow();
error MaxCommitmentAgeTooHigh();

contract ETHRegistrarController is
    Ownable,
    IETHRegistrarController,
    IERC165,
    ERC20Recoverable,
    ReverseClaimer
{
    using StringUtils for *;
    using Address for address;
    using SafeERC20 for IERC20;

    // ============ Constants ============
    uint256 public constant MIN_REGISTRATION_DURATION = 28 days;
    uint256 private constant LIFETIME_DURATION = 31536000000;
    uint256 private constant MAX_REFERRAL_PCT = 30;
    bytes32 private constant ETH_NODE =
        0xe3f6fa8ec34a0592261cf8313365d7f76784e190d0c33fbd4d51f8461a9b8e54;

    // ============ Immutables ============
    BaseRegistrarImplementation public immutable base;
    uint256 public immutable minCommitmentAge;
    uint256 public immutable maxCommitmentAge;
    ReverseRegistrar public immutable reverseRegistrar;
    INameWrapper public immutable nameWrapper;

    // ============ State ============
    TokenPriceOracle public prices;
    ReferralVerifier public referralVerifier;
    address public backendWallet;
    address public infoFi;
    Airdrop public airdrop;
    bool public useAirdrop;
    uint256 public mints;
    uint256 public untrackedInfoFi;

    mapping(bytes32 => uint256) public commitments;
    mapping(address => Token) public verifiedTokens;

    // ============ Structs ============
    struct Token {
        string token;
        address tokenAddress;
    }

    // ============ Events ============
    event NameRegistered(
        string name,
        bytes32 indexed label,
        address indexed owner,
        uint256 baseCost,
        uint256 premium,
        uint256 expires
    );
    event NameRenewed(
        string name,
        bytes32 indexed label,
        uint256 cost,
        uint256 expires
    );

    // ============ Modifiers ============
    modifier onlyBackend() {
        require(msg.sender == backendWallet, "Not Backend");
        _;
    }

    constructor(
        BaseRegistrarImplementation _base,
        TokenPriceOracle _prices,
        uint256 _minCommitmentAge,
        uint256 _maxCommitmentAge,
        ReverseRegistrar _reverseRegistrar,
        INameWrapper _nameWrapper,
        ENS _ens,
        ReferralVerifier _referralVerifier
    ) ReverseClaimer(_ens, msg.sender) {
        if (_maxCommitmentAge <= _minCommitmentAge) {
            revert MaxCommitmentAgeTooLow();
        }
        if (_maxCommitmentAge > block.timestamp) {
            revert MaxCommitmentAgeTooHigh();
        }

        base = _base;
        prices = _prices;
        minCommitmentAge = _minCommitmentAge;
        maxCommitmentAge = _maxCommitmentAge;
        reverseRegistrar = _reverseRegistrar;
        nameWrapper = _nameWrapper;
        referralVerifier = _referralVerifier;
    }

    // ============ Admin ============

    function setAirdrop(Airdrop _airdrop) external onlyOwner {
        airdrop = _airdrop;
    }

    function controlAirdrop(bool enabled) external onlyOwner {
        useAirdrop = enabled;
    }

    function setBackend(address wallet) external onlyOwner {
        backendWallet = wallet;
    }

    function setOracle(address oracle) external onlyOwner {
        prices = TokenPriceOracle(oracle);
    }

    function setReferralVerifier(address _verifier) external onlyOwner {
        referralVerifier = ReferralVerifier(payable(_verifier));
    }

    function setToken(
        Token memory token,
        address tokenAddress
    ) external onlyOwner {
        verifiedTokens[tokenAddress] = token;
    }

    function removeToken(address tokenAddress) external onlyOwner {
        delete verifiedTokens[tokenAddress];
    }

    function setInfoFi(address _infoFi) external onlyOwner {
        infoFi = _infoFi;
    }

    // ============ View Functions ============

    function rentPrice(
        string memory name,
        uint256 duration,
        bool lifetime
    ) public view returns (IPriceOracle.Price memory) {
        require(
            duration == 0 || duration >= MIN_REGISTRATION_DURATION,
            "Invalid duration"
        );
        return
            prices.price(
                name,
                base.nameExpires(uint256(keccak256(bytes(name)))),
                duration,
                lifetime
            );
    }

    function rentPriceToken(
        string memory name,
        uint256 duration,
        string memory token,
        bool lifetime
    ) public view returns (IPriceOracle.Price memory) {
        require(
            duration == 0 || duration >= MIN_REGISTRATION_DURATION,
            "Invalid duration"
        );
        return
            prices.priceToken(
                name,
                base.nameExpires(uint256(keccak256(bytes(name)))),
                duration,
                token,
                lifetime
            );
    }

    function valid(string memory name) public pure returns (bool) {
        return name.strlen() >= 2;
    }

    function available(string memory name) public view returns (bool) {
        bytes32 label = keccak256(bytes(name));
        return valid(name) && base.available(uint256(label));
    }

    function makeCommitment(
        IETHRegistrarController.RegisterRequest calldata req
    ) public pure returns (bytes32) {
        if (req.data.length > 0 && req.resolver == address(0)) {
            revert ResolverRequiredWhenDataSupplied();
        }
        return
            keccak256(
                abi.encode(
                    keccak256(bytes(req.name)),
                    req.owner,
                    req.duration,
                    req.secret,
                    req.resolver,
                    req.data,
                    req.reverseRecord,
                    req.ownerControlledFuses,
                    req.lifetime
                )
            );
    }

    function commit(bytes32 commitment) public {
        if (commitments[commitment] + maxCommitmentAge >= block.timestamp) {
            revert UnexpiredCommitmentExists(commitment);
        }
        commitments[commitment] = block.timestamp;
    }

    // ============ Registration ============

    function register(
        IETHRegistrarController.RegisterRequest calldata req,
        ReferralVerifier.ReferralData calldata referralData,
        bytes calldata referralSignature
    ) external payable {
        (uint256 totalPrice, uint256 expires) = _executeRegistration(req);

        _handleReferral(referralData, referralSignature, totalPrice, false);
        _refundExcess(totalPrice);
        mints++;
    }

    function registerWithCard(
        IETHRegistrarController.RegisterRequest calldata req,
        ReferralVerifier.ReferralData calldata referralData,
        bytes calldata referralSignature
    ) external onlyBackend {
        (uint256 totalPrice, ) = _executeRegistration(req);

        if (mints > 1000) {
            _handleReferral(referralData, referralSignature, totalPrice, true);
        }
        untrackedInfoFi += (totalPrice * 35) / 100;
        mints++;
    }

    function registerWithToken(
        IETHRegistrarController.RegisterRequest calldata req,
        address tokenAddress,
        ReferralVerifier.ReferralData calldata referralData,
        bytes calldata referralSignature
    ) external {
        require(
            verifiedTokens[tokenAddress].tokenAddress != address(0),
            "Unaccepted Token"
        );

        (uint256 totalPrice, ) = _executeTokenRegistration(req, tokenAddress);

        _handleTokenReferral(
            referralData,
            referralSignature,
            totalPrice,
            tokenAddress
        );
        mints++;
    }

    // ============ Internal Registration Logic ============

    function _executeRegistration(
        IETHRegistrarController.RegisterRequest calldata req
    ) internal returns (uint256 totalPrice, uint256 expires) {
        uint256 duration = req.lifetime ? LIFETIME_DURATION : req.duration;

        IPriceOracle.Price memory price = rentPrice(
            req.name,
            duration,
            req.lifetime
        );
        totalPrice = price.base + price.premium;

        if (msg.value < totalPrice) revert InsufficientValue();

        _consumeCommitment(req.name, duration, makeCommitment(req));

        expires = nameWrapper.registerAndWrapETH2LD(
            req.name,
            req.owner,
            duration,
            req.resolver,
            req.ownerControlledFuses
        );

        _finalizeRegistration(req, price, expires);
    }

    function _executeTokenRegistration(
        IETHRegistrarController.RegisterRequest calldata req,
        address tokenAddress
    ) internal returns (uint256 totalPrice, uint256 expires) {
        uint256 duration = req.lifetime ? LIFETIME_DURATION : req.duration;
        Token memory t = verifiedTokens[tokenAddress];

        IPriceOracle.Price memory price = rentPriceToken(
            req.name,
            duration,
            t.token,
            req.lifetime
        );
        totalPrice = price.base + price.premium;

        if (IERC20(tokenAddress).balanceOf(msg.sender) < totalPrice)
            revert InsufficientValue();

        _consumeCommitment(req.name, duration, makeCommitment(req));

        IERC20(tokenAddress).safeTransferFrom(
            msg.sender,
            address(this),
            totalPrice
        );

        expires = nameWrapper.registerAndWrapETH2LD(
            req.name,
            req.owner,
            duration,
            req.resolver,
            req.ownerControlledFuses
        );

        _finalizeRegistration(req, price, expires);
    }

    function _finalizeRegistration(
        IETHRegistrarController.RegisterRequest calldata req,
        IPriceOracle.Price memory price,
        uint256 expires
    ) internal {
        bytes32 label = keccak256(bytes(req.name));

        if (req.data.length > 0) {
            _setRecords(req.resolver, label, req.data);
        }

        if (req.reverseRecord) {
            _setReverseRecord(req.name, req.resolver, msg.sender);
        }

        _updatePoints(
            req.name,
            req.lifetime ? LIFETIME_DURATION : req.duration,
            req.owner,
            req.lifetime
        );

        emit NameRegistered(
            req.name,
            label,
            req.owner,
            price.base,
            price.premium,
            expires
        );
    }

    // ============ Renewal ============

    function renew(
        string calldata name,
        uint256 duration,
        bool lifetime
    ) external payable {
        uint256 actualDuration = lifetime ? LIFETIME_DURATION : duration;
        bytes32 labelhash = keccak256(bytes(name));

        IPriceOracle.Price memory price = rentPrice(
            name,
            actualDuration,
            lifetime
        );
        uint256 totalPrice = price.base + price.premium;

        if (msg.value < totalPrice) revert InsufficientValue();

        uint256 expires = nameWrapper.renew(uint256(labelhash), actualDuration);
        emit NameRenewed(name, labelhash, totalPrice, expires);

        _handleRenewalReferral(labelhash, totalPrice, false);
        _refundExcess(totalPrice);
    }

    function renewCard(
        string calldata name,
        uint256 duration,
        bool lifetime
    ) external onlyBackend {
        uint256 actualDuration = lifetime ? LIFETIME_DURATION : duration;
        bytes32 labelhash = keccak256(bytes(name));

        IPriceOracle.Price memory price = rentPrice(
            name,
            actualDuration,
            lifetime
        );
        uint256 totalPrice = price.base + price.premium;

        uint256 expires = nameWrapper.renew(uint256(labelhash), actualDuration);
        emit NameRenewed(name, labelhash, totalPrice, expires);

        _handleRenewalReferral(labelhash, totalPrice, true);
        untrackedInfoFi += (totalPrice * 35) / 100;
    }

    function renewTokens(
        string calldata name,
        uint256 duration,
        address tokenAddress,
        bool lifetime
    ) external {
        require(
            verifiedTokens[tokenAddress].tokenAddress != address(0),
            "Unaccepted Token"
        );

        uint256 actualDuration = lifetime ? LIFETIME_DURATION : duration;
        bytes32 labelhash = keccak256(bytes(name));
        Token memory t = verifiedTokens[tokenAddress];

        IPriceOracle.Price memory price = rentPriceToken(
            name,
            actualDuration,
            t.token,
            lifetime
        );
        uint256 totalPrice = price.base + price.premium;

        if (IERC20(tokenAddress).balanceOf(msg.sender) < totalPrice)
            revert InsufficientValue();

        IERC20(tokenAddress).safeTransferFrom(
            msg.sender,
            address(this),
            totalPrice
        );

        uint256 expires = nameWrapper.renew(uint256(labelhash), actualDuration);
        emit NameRenewed(name, labelhash, totalPrice, expires);

        _handleRenewalTokenReferral(labelhash, totalPrice, tokenAddress);
    }

    // ============ Referral Handlers ============

    function _handleReferral(
        ReferralVerifier.ReferralData calldata referralData,
        bytes calldata referralSignature,
        uint256 totalPrice,
        bool isFiat
    ) internal {
        if (
            referralData.referrer == address(0) || referralSignature.length == 0
        ) return;

        uint256 amount = (totalPrice * MAX_REFERRAL_PCT) / 100;

        if (!isFiat) {
            referralVerifier.processReferral{value: amount}(
                referralData,
                referralSignature,
                totalPrice,
                address(0),
                false
            );
        } else {
            referralVerifier.processReferral(
                referralData,
                referralSignature,
                totalPrice,
                address(0),
                true
            );
        }
    }

    function _handleTokenReferral(
        ReferralVerifier.ReferralData calldata referralData,
        bytes calldata referralSignature,
        uint256 totalPrice,
        address tokenAddress
    ) internal {
        if (
            referralData.referrer == address(0) || referralSignature.length == 0
        ) return;

        uint256 amount = (totalPrice * MAX_REFERRAL_PCT) / 100;
        IERC20(tokenAddress).safeTransfer(address(referralVerifier), amount);

        referralVerifier.processReferral(
            referralData,
            referralSignature,
            totalPrice,
            tokenAddress,
            false
        );
    }

    function _handleRenewalReferral(
        bytes32 nameHash,
        uint256 totalPrice,
        bool isFiat
    ) internal {
        (, address originalReferrer, bool isValid, , ) = referralVerifier
            .getDomainReferralInfo(nameHash);
        if (originalReferrer == address(0) || !isValid) return;

        uint256 amount = (totalPrice * MAX_REFERRAL_PCT) / 100;

        if (!isFiat) {
            referralVerifier.processRenewalReferral{value: amount}(
                nameHash,
                totalPrice,
                address(0),
                false
            );
        } else {
            referralVerifier.processRenewalReferral(
                nameHash,
                totalPrice,
                address(0),
                true
            );
        }
    }

    function _handleRenewalTokenReferral(
        bytes32 nameHash,
        uint256 totalPrice,
        address tokenAddress
    ) internal {
        (, address originalReferrer, bool isValid, , ) = referralVerifier
            .getDomainReferralInfo(nameHash);
        if (originalReferrer == address(0) || !isValid) return;

        uint256 amount = (totalPrice * MAX_REFERRAL_PCT) / 100;
        IERC20(tokenAddress).safeTransfer(address(referralVerifier), amount);

        referralVerifier.processRenewalReferral(
            nameHash,
            totalPrice,
            tokenAddress,
            false
        );
    }

    // ============ Internal Utilities ============

    function _refundExcess(uint256 totalPrice) internal {
        if (msg.value > totalPrice) {
            (bool success, ) = payable(msg.sender).call{
                value: msg.value - totalPrice
            }("");
            require(success, "Refund failed");
        }
    }

    function _updatePoints(
        string memory name,
        uint256 duration,
        address owner,
        bool lifetime
    ) internal {
        if (!useAirdrop) return;

        uint256 len = name.strlen();
        uint256 point = len == 2
            ? 100
            : len == 3
                ? 50
                : len == 4
                    ? 20
                    : 10;
        uint256 points = point * (lifetime ? 3 : duration / 31536000);

        airdrop.updatePoints(owner, points);
    }

    function _consumeCommitment(
        string memory name,
        uint256 duration,
        bytes32 commitment
    ) internal {
        if (commitments[commitment] + minCommitmentAge > block.timestamp) {
            revert CommitmentTooNew(commitment);
        }
        if (commitments[commitment] + maxCommitmentAge <= block.timestamp) {
            revert CommitmentTooOld(commitment);
        }
        if (!available(name)) {
            revert NameNotAvailable(name);
        }

        delete commitments[commitment];

        if (duration != 0 && duration < MIN_REGISTRATION_DURATION) {
            revert DurationTooShort(duration);
        }
    }

    function _setRecords(
        address resolverAddress,
        bytes32 label,
        bytes[] memory data
    ) internal {
        bytes32 nodehash = keccak256(abi.encodePacked(ETH_NODE, label));
        Resolver(resolverAddress).multicallWithNodeCheck(nodehash, data);
    }

    function _setReverseRecord(
        string memory name,
        address resolver,
        address owner
    ) internal {
        reverseRegistrar.setNameForAddr(
            msg.sender,
            owner,
            resolver,
            string.concat(name, ".id")
        );
    }

    // ============ Withdraw ============

    function withdraw() external onlyOwner {
        (bool success, ) = payable(owner()).call{value: address(this).balance}(
            ""
        );
        require(success, "Transfer failed");
    }

    function withdrawTokens(address tokenAddress) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(
            owner(),
            IERC20(tokenAddress).balanceOf(address(this))
        );
    }

    function resetInfoFi() external payable onlyOwner {
        require(msg.value > 0, "Must send value");
        (bool ok, ) = payable(infoFi).call{value: msg.value}("");
        require(ok, "Payment failed");
        untrackedInfoFi = 0;
    }

    // ============ View ============

    function getMints() external view returns (uint256) {
        return mints;
    }

    function supportsInterface(
        bytes4 interfaceID
    ) external pure returns (bool) {
        return
            interfaceID == type(IERC165).interfaceId ||
            interfaceID == type(IETHRegistrarController).interfaceId;
    }
}

